      # Extract specific information using grep and awkget_memory_info() {
    sudo dmidecode -t 17 | awk '
    /Memory Device/,/^$/ {
        if (/Size:/) size=$2" "$3
        if (/Speed:/) speed=$2" "$3
        if (/Manufacturer:/) manufacturer=$2
        if (/Part Number:/) part=$3
        if (/Locator:/ && !/Bank/) {
            locator=$2
            if (size != "No Module") {
                print locator": "size" @ "speed" ("manufacturer" "part")"
            }
        }
    }'
}

