```go
"Mappings":{
     "InstanceVolumeSize" : {
            "Production" : {
                "VolumeSize" : "100"    #This is the size of EBS in GB
            }
    }

"Resources": {
        "testLaunchConfig": {
            "Type": "AWS::AutoScaling::LaunchConfiguration",
            "Properties": {
                "BlockDeviceMappings" : [
                    {
                           "DeviceName" : "/dev/sda1",                        
                            "Ebs" : {
                            "VolumeSize" : {
                                "Fn::FindInMap" : [ "InstanceVolumeSize", "Production", "VolumeSize" ]
                            },
                            "VolumeType" : "gp2",
                            "DeleteOnTermination": "true"
                        }
                    }
                ]
      }
}
```
